tinymce.PluginManager.add('aksess_insertlink', function(editor, url){

    function makeLinkPopupWindow() {
        var selection = editor.selection;
        // No selection and not in link
        if (selection.isCollapsed() && !editor.dom.getParents(selection.getNode(), 'A')) {
            return;
        }
        var href = '';
        var anchor = '';
        var newWindow = false;
        var mailTitle = '';
        var elm0 = selection.getNode();
        console.log("elm0: "+elm0);
        //console.log("elM: "+elm.nodeName);
        //elm = editor.selection.getContent();
        //console.log("elM2: "+elm);
        var elm = editor.dom.getParent(selection.getNode(), "A");
        console.log("elm: "+elm);
        if (elm != null && elm.nodeName == "A") {
            console.log("if only..");
            console.log("elms: "+elm.nodeName);
            href = editor.dom.getAttrib(elm, 'href');
            var onclick = editor.dom.getAttrib(elm, 'onclick');
            if (onclick.indexOf('window.open') != -1) {
                newWindow = true;
            }
            mailTitle = editor.dom.getAttrib(elm, 'subject');
        }

        // IE 7 & 8 looses selection. Must be kept and restored manually.
        editor.focus();
        editor.windowManager.bookmark = selection.getBookmark();//1);
        //var bm = selection.getBookmark();//1);

        console.log("elm: "+elm);
        console.log("href: "+href);
        console.log("mail subject: "+mailTitle);
        editor.windowManager.open({
            title:"Sett inn lenke",
            url: properties.contextPath +"/"+ "publish/popups/InsertLink.action?url=" + encodeURI(href) + "&isOpenInNewWindow=" + encodeURI(newWindow) + "&isMiniAdminMode=" + encodeURI(miniAdminMode) + "&subjectTitle=" + encodeURI(mailTitle), //fjern subject??
            iframe: false,
            //onclose: function(){
            //    console.log("ONCLOSE!");
            //    console.log(bm);
            //    selection.moveToBookmark(bm);
            //},
            width: 660,
            height:305
        });
    }

    function unLink() {
        var selection = editor.selection;
        if (!editor.dom.getParents(selection.getNode(), 'A')) {
            return;
        }
        var textContent = selection.getContent();
        var elm = editor.dom.getParent(selection.getNode(), 'A' );

        // Store bookmark
        editor.focus();
        editor.windowManager.bookmark = selection.getBookmark(); //1);

        // Remove whole selection
        editor.dom.remove(editor.dom.getParent(selection.getNode(),'A'));
        // Insert text
        editor.execCommand("mceBeginUndoLevel");
        editor.execCommand("mceInsertRawHTML", false, textContent);
        editor.execCommand("mceEndUndoLevel");

        console.log("unlink elm: "+elm);

        //editor.selection.collapse();
        //editor.dom.remove('A');
        //editor.dom.text
        //editor.selection.setNode( editor.dom.create( '', {}, textContent) );
    }

    // Add a button that opens a window
    editor.addButton('aksess_insertlink', {
        tooltip: 'Insert/edit image',
        icon: 'link',
        shortcut:'Meta+K',
        onclick: makeLinkPopupWindow,
        stateSelector:"a[href]"
    });

    editor.addButton("aksess_unlink", {
        tooltip:"Remove link",
        icon:"unlink",
        onclick: unLink,
        stateSelector:"a[href]"
    });

    // Adds a new item to the insert menu
    editor.addMenuItem('aksess_insertlink', {
        text: 'Insert/edit link',
        icon: 'link',
        shortcut:'Meta+K',
        context: 'insert',
        stateSelector:"a[href]",
        onclick: makeLinkPopupWindow
    });
});
