tinymce.PluginManager.add('aksess_spellchecker', function(editor, url){

    var active = false;
    var contextmenuString = '';
    function spellcheck(){
        editor.getBody().spellcheck = active = !active;
        console.log("spellcheck?: "+ editor.getBody().spellcheck);
        if(active){
            contextmenuString = editor.settings.contextmenu;
            editor.settings.contextmenu = false;
            //possibly force browserspellcheck without need for blankspace
        } else {
            editor.settings.contextmenu = contextmenuString;
        }
    }

    editor.addMenuItem('aksess_spellchecker', {
        text: 'Spellcheck',
        icon: 'spellchecker',
        context: 'tools',
        stateSelector: active,
        onclick: spellcheck,
        selectable: true
    });

    editor.addButton('aksess_spellchecker', {
        tooltip: 'Spellcheck',
        icon: 'spellchecker',
        stateSelector: active,
        onclick: spellcheck
    });
    editor.addCommand('mceSpellCheck', spellcheck);

});