tinymce.PluginManager.add('aksess_template', function(editor, url){

    function makeLinkPopupWindow() {
        var selection = editor.selection;

        // IE 7 & 8 looses selection. Must be kept and restored manually.
        editor.focus();
        editor.windowManager.bookmark = selection.getBookmark();

        editor.windowManager.open({
            title:"Sett inn lenke",
            url : url + '/template.htm',
            iframe: false,
            width: 660,
            height:305
        });
    }

    // Add a button that opens a window
    editor.addButton('aksess_template', {
        tooltip: 'Insert/edit image',
        icon: 'link',
        onclick: makeLinkPopupWindow
    });

    // Adds a new item to the insert menu
    editor.addMenuItem('aksess_template', {
        text: 'Insert/edit link',
        icon: 'template',
        context: 'insert',
        onclick: makeLinkPopupWindow
    });
});
